  # recipes_jsons << recipe

  puts 'creating recipes'

  new_recipe = Recipe.new(
            name: recipe["title"],
            prep_time: recipe["preparationMinutes"] || 5,
            cook_time: recipe["cookingMinutes"] || 5,
            servings: recipe["servings"],
            photo: recipe["image"] || 'https://source.unsplash.com/1600x900/?vegan',
            difficulty: ["Easy", "Moderate", "Hard"].sample)

  recipe["extendedIngredients"].each do |do_ingredient|
    ingredient = Ingredient.find_or_create_by(name: do_ingredient["name"], unit_of_measure: do_ingredient["measures"]["metric"]["unitShort"], category: do_ingredient["aisle"].split(';').first)
    quantity = do_ingredient["measures"]["metric"]["amount"].ceil

    recipe_item = RecipeItem.find_by(recipe: new_recipe, ingredient: ingredient)

    if recipe_item
      recipe_item.quantity += quantity
      recipe_item.save
    else
      new_recipe.recipe_items << RecipeItem.new(
        quantity: quantity,
        ingredient: ingredient
      )
    end
  end

  recipe["analyzedInstructions"][0]["steps"].each do |step|
    new_recipe.steps << Step.new(
      content: step["step"],
      order: step["number"]
    )
  end

  recipe["dishTypes"].each do |dish_type|
    new_recipe.tag_items << TagItem.new(
      tag: Tag.find_or_create_by(name: dish_type)
    )
    puts dish_type
  end
  puts new_recipe.tag_items

  puts 'saving recipe user'
  new_recipe.user = nic

  puts 'saving recipe'
  new_recipe.save!



  rand(8).times do

    # Calculate a random review
    chance = rand(100)

    if chance > 70
      rating = 5
    elsif chance > 40
      rating = 4
    elsif chance > 20
      rating = 3
    elsif chance > 5
      rating = 2
    else
      rating = 1
    end

    new_review = Review.new(
      content: reviews.sample,
      rating: rating
      )
    new_review.recipe = new_recipe
    new_review.user = users.sample
    new_review.date = DateTime.now
    new_review.save!
  end

  puts 'Done creating recipes, steps and ingredients'
end

